import { useState } from 'react';
import axios from 'axios';
import { API_BASE_URL } from './apiConfig'; // Путь к вашему конфигу с URL API

const useRegistration = () => {
  const [formData, setFormData] = useState({
    firstName: '',
    lastName: '',
    email: '',
    password: '',
  });

  const [loading, setLoading] = useState(false); // Состояние загрузки
  const [message, setMessage] = useState('');
  const [error, setError] = useState('');

  const handleChange = (e) => {
    const { name, value, type, checked } = e.target;
    setFormData({
      ...formData,
      [name]: type === 'checkbox' ? checked : value,
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true); // Начинаем загрузку
    setError(''); // Сбрасываем ошибки
    setMessage(''); // Сбрасываем сообщения

    try {
      const response = await axios.post(`${API_BASE_URL}/users`, formData);
      setMessage(response.data.message); // Успешное сообщение
      setLoading(false); // Заканчиваем загрузку
    } catch (err) {
      setError(err.response?.data?.message || 'Ошибка регистрации');
      setLoading(false); // Заканчиваем загрузку в случае ошибки
    }
  };

  return {
    formData,
    handleChange,
    handleSubmit,
    loading,
    message,
    error,
  };
};

export default useRegistration;
